script:
frappe.msgprint("Server Script Triggered")

total_late_hours = 0

attendance_list = frappe.get_all(
    "Attendance",
    filters={
        "employee": doc.employee,
        "attendance_date": ["between", [doc.start_date, doc.end_date]],
        "status": "Present",
        "late_entry": 1  # Add this condition to filter only records marked as Late Entry
    },
    fields=["attendance_date", "in_time", "shift", "late_entry"]
)

frappe.msgprint("Attendance rows with Late Entry: " + str(len(attendance_list)))

for att in attendance_list:

    if not att.get("shift") or not att.get("in_time"):
        continue

    try:
        shift_doc = frappe.get_doc("Shift Type", att.get("shift"))
    except:
        continue

    if not shift_doc.get("start_time"):
        continue

    try:
        shift_start = frappe.utils.get_datetime(
            f"{att.get('attendance_date')} {shift_doc.get('start_time')}"
        )
        in_dt = frappe.utils.get_datetime(att.get("in_time"))

        late_hours = frappe.utils.time_diff_in_hours(in_dt, shift_start)

        frappe.msgprint(f"LATE HOURS: {late_hours}")

        if late_hours > 0:
            total_late_hours = total_late_hours + late_hours

    except Exception as e:
        frappe.msgprint("Error: " + str(e))
        continue

frappe.msgprint("FINAL TOTAL LATE HOURS = " + str(total_late_hours))

doc.custom_late_entry_hours = round(total_late_hours, 2)
doc.custom_working_hours_shortfall = round(total_late_hours, 2)

# Set subject based on machine problem or others
if doc.custom_machine_problem:
    doc.subject = f"{doc.custom_machine_problem} - {doc.ticket_type}"
else:
    doc.subject = f"{doc.custom_others} - {doc.ticket_type}"

# Set custom_created_date from creation (Created On)
if not doc.custom_created_date:
    doc.custom_created_date = doc.creation
