css:
.print-format {
    margin-left: 4mm;
    margin-right: 4mm;
    font-size: 11px;
}

.right {
    text-align: right;
}

.center {
    text-align: center;
}

.inline {
    display: inline;
}

.inline div {
    display: inline;
}

.inline p {
    display: inline;
}

.no-break-inside {
    page-break-inside: avoid;
}

.main {
    width: 100%;
}

.uppercase {
    text-transform: uppercase;
}

.print-format td {
    padding: 4px !important;
}

table {
    width: 100%;
}

span.no-value {
    color: #c9c9c9;
}

.header {
    margin-top: -12mm; /* to reduce the margin top of header */
    font-size: 13px;
}

.header td {
    width: 50%;
}

.header td:last-child {
    text-align: right;
}

.header td:first-child > div {
    border-left: 1mm solid #ba152b;
    padding-left: 2mm;
}

.header .company-name {
    font-size: 15px;
    font-weight: bold;
    color: #005ba8;
}

.header p {
    margin: 0px !important;
}

.logo {
    width: 280px;
    height: 70px;
    margin-right: 5px;
    margin-bottom: 5px;
}

.content {
    border-left: 1px solid black;
    border-right: 1px solid black;
}

.invoice-text {
    font-weight: bold;
    font-size: 17px;
    text-align: center;
    border-top: 1px solid black;
    border-bottom: 1px solid black;
    padding-top: 2mm;
}

.invoice-info {
    border-bottom: 1px solid black;
}

.invoice-info td {
    padding-top: 1px !important;
    padding-bottom: 1px !important;
}

.invoice-info td:first-child, .invoice-info td:nth-child(3) {
    width: 20%;
    font-weight: bold;
}

.invoice-info td:nth-child(2), .invoice-info td:nth-child(4) {
    width: 30%;
}

.invoice-info tr:nth-child(even) {
    background-color: #ededed;
}

.party-info {
    margin-top: 3mm;
    margin-bottom: 3mm;
    border-top: 1px solid black;
    border-bottom: 1px solid black;
}

.party-info tr:first-child td {
    font-weight: bold;
    background-color: #ededed;
}

.party-info tr td {
    border-top: 1px solid black;
    border-bottom: 1px solid black;
}

.party-info tr td:first-child {
    border-right: 1px solid black;
}

.party-info tr td:nth-child(2) {
    border-left: 1px solid black;
}

.party-info .party-gstin-info span:first-child {
    white-space: nowrap;
    float: left;
}

.party-info .party-gstin-info span:nth-child(2) {
    white-space: nowrap;
    float: right;
}

.items tr td:not(:last-child) {
    border-right: 1px solid black;
}

.items thead tr td {
    font-weight: bold;
    text-align: center;
    border-top: 1px solid black;
    border-bottom: 1px solid black;
}

.items tbody tr td:first-child {
    width: 5mm;
    white-space: nowrap;
}

.items tbody tr td:nth-child(3) {
    width: 16mm;
    white-space: nowrap;
}

.items tbody tr td:nth-child(4) {
    width: 10mm;
    white-space: nowrap;
}

.items tbody tr td:nth-child(5) {
    width: 16mm;
    white-space: nowrap;
}

.items tbody tr td:nth-child(6) {
    width: 10mm;
    white-space: nowrap;
}

.items tbody tr td:nth-child(7) {
    width: 18mm;
    white-space: nowrap;
    background-color: #ededed;
}

.items tbody tr td:nth-child(8) {
    width: 10mm;
    white-space: nowrap;
}

.items tbody tr td:nth-child(9) {
    width: 18mm;
    white-space: nowrap;
    background-color: #ededed;
}

.items .item-end-star {
    text-align: center;
}

.items tfoot td {
    border-top: 1px solid black;
    border-bottom: 1px solid black;
    background-color: #ededed;
}

.amounts-table .gst-td {
    width: 70%;
}

.amounts-table .totals-td {
    width: 30%;
}

.amounts-table .gst-td {
    background-color: #ededed;
}

.amounts-table .gst-td,
.amounts-table .totals-td {
    border-top: 1px solid black;
    border-bottom: 1px solid black;
    padding: 0 !important;
}

.amounts-table .gst-td tr:first-child td {
    border-bottom: 1px solid black;
    font-weight: bold;
    text-align: center;
    background-color: #ededed;
}

.amounts-table .gst-td tr:last-child td {
    border-top: 1px solid black;
}

.amounts-table .totals-td tr:first-child td,
.amounts-table .totals-td tr:last-child td {
    font-weight: bold;
}

.amounts-table .totals-td tr td {
    border: 1px solid black;
}

.amounts-table .totals-td tr:first-child td {
    border-top: none;
}

.amounts-table .totals-td tr:last-child td {
    border-bottom: none;
}

.amounts-table .totals-td tr td:last-child {
    border-right: none;
}

.amounts-table .gst-amt-field {
    background-color: #f9f9f9;
}

.bottom-content .bottom-content-col {
    width: 33.33%;
    border-bottom: 1px solid black;
}

.bottom-content .bottom-content-col:not(:last-child) {
    border-right: 1px solid black;
}

.bottom-content .bank-info tr:first-child td,
.bottom-content .outstanding-info tr:first-child td {
    text-align: center;
    font-weight: bold;
}
.signatures td {
    width: 25%;
}

.signatures tr:first-child {
    height: 20mm;
}

.signatures tr:last-child div {
    margin: 4mm;
    padding-top: 1mm;
    border-top: 1px solid black;
    white-space: nowrap;
    text-align: center;
    font-weight: bold;
}

.signatures tr:last-child td {
    border-bottom: 1px solid black;
}

.invoice-info tr td:first-child,
.party-info tr td,
.amt-in-words,
.gst-td tr:last-child td:first-child,
.bottom-content tr td:first-child {
    padding-left: 5px !important;
}
