css:
body {
	padding: 0 !important;
}

.print-format {
    margin-left: 4mm;
    margin-right: 4mm;
    font-size: 10px;
}

.right {
    text-align: right;
}

.center {
    text-align: center;
}

.inline {
    display: inline;
}

.inline div {
    display: inline;
}

.inline p {
    display: inline;
}

.no-break-inside {
    page-break-inside: avoid;
}

.va-mid,
.va-mid td {
    vertical-align: middle !important;
}

.main {
    width: 100%;
    page-break-before: always;
}

.uppercase {
    text-transform: uppercase;
}

.print-format td {
    padding: 4px !important;
}

table {
    width: 100%;
}

span.no-value {
    color: #c9c9c9;
}

.header {
    font-size: 13px;
}

.header tr:first-child td {
    height: 5mm;
}

.header td {
    width: 50%;
}

.header td:last-child {
    text-align: right;
}

.header td:first-child > div {
    border-left: 1mm solid #ba152b;
    padding-left: 2mm;
}

.header .company-name {
    font-size: 15px;
    font-weight: bold;
    color: #005ba8;
}

.header p {
    margin: 0px !important;
}

.logo {
    width: 280px;
    height: 70px;
    margin-right: 5px;
    margin-bottom: 5px;
}

.content {
    /*border-left: 1px solid black;*/
    /*border-right: 1px solid black;*/
}

.invoice-text {
    font-weight: bold;
    font-size: 17px;
    text-align: center;
    border: 1px solid black;
    padding-top: 2mm;
    position: relative;
}

.invoice-header-text {
    float: right;
    font-size: 13px;
    font-weight: bold;
    color: #1163ac;
    position: absolute;
    right: 0;
    top: 0;

}

.invoice-info {
    border: 1px solid black;
    border-top: 0;
}

.invoice-info td {
    padding-top: 1px !important;
    padding-bottom: 1px !important;
    border-bottom: 1px solid black;
}

.invoice-info td:first-child, .invoice-info td:nth-child(3) {
    width: 20%;
    font-weight: bold;
}

.invoice-info td:nth-child(2) {
    border-right: 1px solid black;
}

.invoice-info td:nth-child(2), .invoice-info td:nth-child(4) {
    width: 30%;
}

.invoice-info tr:nth-child(even) {
    background-color: #ededed;
}

.empty-space {
    width: 100%;
    height: 3mm;
    border-left: 1px solid black;
    border-right: 1px solid black;
}

.party-info {
    border: 1px solid black;
}

.party-info tr:first-child td {
    font-weight: bold;
    background-color: #ededed;
}

.party-info tr td {
    border-top: 1px solid black;
    border-bottom: 1px solid black;
}

.party-info tr td:first-child {
    border-right: 1px solid black;
}

.party-info tr td:nth-child(2) {
    border-left: 1px solid black;
}

.party-info .party-gstin-info span:first-child {
    white-space: nowrap;
    float: left;
}

.party-info .party-gstin-info span:nth-child(2) {
    white-space: nowrap;
    float: right;
}

.items td {
    padding-top: 2px !important;
    padding-bottom: 2px !important;
}

.items tr td {
    border-right: 1px solid black;
}

.items tr td:first-child {
    border-left: 1px solid black;
}

.items thead tr td {
    font-weight: bold;
    text-align: center;
    border-top: 1px solid black;
    border-bottom: 1px solid black;
}

.items tbody tr td:first-child {
    width: 5mm;
}

.items tbody tr td:nth-child(3) {
    width: 16mm;
}

.items tbody tr td:nth-child(4) {
    width: 10mm;
}

.items tbody tr td:nth-child(5) {
    width: 16mm;
}

.items tbody tr td:nth-child(6) {
    width: 10mm;
}

.items tbody tr td:nth-child(7) {
    width: 18mm;
    background-color: #ededed;
}

.items tbody tr td:nth-child(8) {
    width: 10mm;
}

.items tbody tr td:nth-child(9) {
    width: 18mm;
    background-color: #ededed;
}

.items .item-end-star {
    text-align: center;
}

.items tfoot td {
    border-top: 1px solid black;
    border-bottom: 1px solid black;
    background-color: #ededed;
}

.item-description .ql-editor {
    line-height: 13px;
}

.amt-in-words {
    padding-top: 1mm;
    border: 1px solid black;
    margin-top: -1px;
}

.amounts-table {
    border-left: 1px solid black;
    border-right: 1px solid black;
}

.amounts-table .gst-td {
    width: 70%;
}

.amounts-table .totals-td {
    width: 30%;
}

.amounts-table .gst-td {
    background-color: #ededed;
}

.amounts-table .gst-td,
.amounts-table .totals-td {
    padding: 0 !important;
}

.amounts-table .gst-td tr:first-child td {
    border-bottom: 1px solid black;
    font-weight: bold;
    text-align: center;
    background-color: #ededed;
}

.amounts-table .gst-td tr:last-child td {
    border-top: 1px solid black;
}

.amounts-table .gst-td {
    border-right: 1px solid black;
}

.amounts-table .totals-td {
    border-left: 1px solid black;
}

.amounts-table .totals-td tr:first-child td,
.amounts-table .totals-td tr:last-child td {
    font-weight: bold;
}

.amounts-table .totals-td tr td {
    border: 1px solid black;
}

.amounts-table .totals-td tr:first-child td {
    border-top: none;
}

.amounts-table .totals-td tr:last-child td {
    border-bottom: -1px solid black;
}


.amounts-table .totals-td tr td:last-child {
    border-right: none;
}

.amounts-table .totals-td tr td:first-child {
    border-left: none;
}

.amounts-table .gst-amt-field {
    background-color: #f9f9f9;
}

.bottom-content {
    margin-top: -1px;
}

.bottom-content .bottom-content-col {
    width: 33.33%;
    border: 1px solid black;
}

.bottom-content .bottom-content-col:not(:last-child) {
    border-right: 1px solid black;
}

.bottom-content .bank-info tr:first-child td,
.bottom-content .outstanding-info tr:first-child td {
    text-align: center;
    font-weight: bold;
}
.signatures td {
    width: 25%;
}

.signatures tr:first-child,
.signatures .company-seal {
    margin-top: 4mm;
    text-align: left;
    height: 20mm;
}

.signatures tr:last-child div {
    margin-left: 4mm;
    margin-right: 4mm;
    padding-top: 1mm;
    border-top: 1px solid black;
    text-align: center;
    font-weight: bold;
}

.signatures {
    border: 1px solid black;
    border-top: 0;
}

.invoice-info tr td:first-child,
.party-info tr td,
.amt-in-words,
.gst-td tr:last-child td:first-child,
.bottom-content tr td:first-child {
    padding-left: 5px !important;
}

.footer {
    height: 10mm;
    font-size: 14px;
}

.serial-no-list {
    page-break-before: always;
    border-collapse: collapse;
}

.serial-no-list .serial-party-info {
    width: 50%;
    font-weight: normal;
}

.bill-to {
    float: left;
    height: 100%;
}

.ship-to {
    float: right;
    height: 100%;
}



.serial-no-list td {
    border: 1px solid black;
    padding-top: 3px !important;
    padding-bottom: 3px !important;
}

.serial-no-list thead td {
    font-weight: bold;
}

.serial-no-list thead tr:nth-child(1) td,
.serial-no-list thead tr:nth-child(2) td {
    font-size: 15px;
}

.serial-no-list thead tr:nth-child(4) td {
    text-align: center;
}

.serial-no-list tbody tr:last-child td div {
    height: 15mm;
}

.serial-no-list tr td:first-child {
    width: 7%;
}
