css:
body {
	padding: 0 !important;
}

.print-format {
    margin-left: 4mm;
    margin-right: 4mm;
    font-size: 9px;
}

.right {
    text-align: right;
}

.center {
    text-align: center;
}

.no-break {
    page-break-inside: avoid;
}

.main {
    width: 100%;
}

.empty-header {
    height: 7mm;
}

.print-format td {
    padding: 4px !important;
}

.print-format p {
    margin: 0;
}

.u {
    text-decoration: underline;
    text-underline-offset: 3px    
}

table {
    width: 100%;
}

.header td {
    padding-left: 0 !important;
}

td.header-left,
td.header-right {
    padding-top: 0 !important;
}

.header-left {
    width: 68%;
}

.header-right {
    width: 32%;
}

.header-left table td:first-child {
    width: 17%;
}

.header-left img {
    /*margin-top: 5mm;*/
    width: 130px;
    /*height: 30px;*/
}

.header-left table tr:nth-child(2) p {
    font-weight: bold;
    font-size: 15px;
}

.header-left table tr:nth-child(3) td {
    color: red;
}

.header-left table tr:nth-child(3) td:first-child,
.header-left table tr:nth-child(4) td {
    font-weight: bold;
}

.header-left table tr:nth-child(1) td,
.header-left table tr:nth-child(3) td,
.header-left table tr:nth-child(4) td {
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}

.header-left table tr:nth-child(5) td {
    padding-top: 4mm !important;
    font-weight: bold;
    font-size: 10px;
}

.header-right img {
    width: 240px;
    /*height: 50px;*/
}

.header-right > div {
    padding-top: 4px;
    line-height: 12px;
}

.party-info td {
    padding: 0 !important;
}

.party-info tr td:first-child {
    width: 11.56%;
}

.party-info tr td:nth-child(3),
.party-info tr td:nth-child(4){
    width: 16%;
}

.party-info tr:nth-child(3) td {
    height: 3mm;
}

.party-name-addr,
.delivery-addr {
    display: inline-block;
    text-transform: uppercase;
    float: left;
}

.party-name-addr > div:first-child {
    float: left;
    display: inline-block;
}

.party-name-addr > div:nth-child(2) {
    float: right;
    display: inline-block;
}

.delivery-addr {
    margin-left: 5mm;
}

.inline {
    display: inline;
}

.inline div {
    display: inline;
}

.inline p {
    display: inline;
}

.header-preceeding-space {
    float: left;
    visibility: hidden;
}

.items {
    margin-top: 6mm;
}

.items td {
    border-left: 1px solid black;
}

.items tr td:last-child {
    border-right: 1px solid black;
}

.items thead td {
    border: 1px solid black;
    font-weight: bold;
    background-color: #ededed;
    vertical-align: middle !important;
}

.items tbody tr td:first-child {
    width: 5mm;
}

.items tbody tr td:nth-child(2) {
    width: 10mm;
}

.items tbody tr td:nth-child(3) {
    width: 35mm;
}

.items tbody tr td:nth-child(5) {
    width: 20mm;
}

.items tbody tr td:nth-child(6) {
    width: 20mm;
}

.items tfoot td {
    border: 1px solid black;
    font-weight: bold;
}

.item-description .ql-editor {
    line-height: 10px;
}

.empty-line td {
    height: 0.5mm;
    padding: 0px !important;
}

.bottom-content {
    margin-top: 3mm;
}

.bottom-content > table td {
    padding: 0 !important;
}

.bc-left, .bc-right {
    width: 50%;
}

.bc-right {
    width: 50%;
}

.bc-left table tr td:first-child {
    width: 25%;
}

.payment-terms tr td:first-child {
    width: 12.5%;
}

.bottom-content > table.space-above td {
    padding-top: 6px !important;
}

.bottom-content > table tr.bc-terms-space-above td {
    padding-top: 6px !important;
}

.terms table,
.terms table tr,
.terms table td {
    border: 0px !important;
    padding-left: 0px !important;
}

.terms table {
    margin: 2mm 0 !important;
}

.signature {
    margin-top: 3mm;
}

.signature tr td:nth-child(2) {
    width: 30%;
}

.signature tr:first-child {
    min-height: 25mm;
}

.signature td div {
    width: 50mm;
    border-top: 1px solid black;
    text-align: left;

}

.signature .sign-company-name {
    font-size: 10px;
}

.signature .company-seal,
.signature .sales-person-sign {
    width: 30mm;
}

.footer {
    height: 10mm;
}
