css:
.print-format {
    margin-left: 4mm;
    margin-right: 4mm;
    font-size: 10px;
}

.right {
    text-align: right;
}

.center {
    text-align: center;
}

.no-break-inside {
    page-break-inside: avoid;
}

.main {
    width: 100%;
}


.print-format td {
    padding: 4px !important;
}

.print-format p {
    margin: 0;
}

.u {
    text-decoration: underline;
    text-underline-offset: 3px    
}

table {
    width: 100%;
}

.header {
    margin-top: -12mm; /* to reduce the margin top of header */
}

.header-left {
    width: 68%;
}

.header-right {
    width: 32%;
}

.header-left table td:first-child {
    width: 17%;
}

.header-left img {
    margin-top: 5mm;
    width: 150px;
    height: 45px;
}

.header-left table tr:nth-child(2) p {
    font-weight: bold;
    font-size: 15px;
}

.header-left table tr:nth-child(3) td {
    color: red;
}

.header-left table tr:nth-child(3) td:first-child,
.header-left table tr:nth-child(4) td {
    font-weight: bold;
}

.header-left table tr:nth-child(1) td,
.header-left table tr:nth-child(3) td,
.header-left table tr:nth-child(4) td {
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}

.header-left table tr:nth-child(5) td {
    padding-top: 4mm !important;
    font-weight: bold;
    font-size: 11px;
}

.header-right img {
    width: 240px;
    height: 60px;
}

.header-right > div {
    padding-left: 5px;
    line-height: 12px;
}

.party-info td {
    padding: 0px !important;
}

.party-info tr td:first-child {
    width: 11.56%;
}

.party-info tr td:nth-child(3),
.party-info tr td:nth-child(4){
    width: 16%;
}

.party-info tr td:nth-child(2) {
    padding-left: 6px !important;
}

.party-info tr:not(:first-child) td:nth-child(2) {
    padding-left: 12px !important;
}

.party-name-addr {
    display: inline-block;
}

.party-name-addr > div:first-child {
    float: left;
    display: inline-block;
}

.party-name-addr > div:nth-child(2) {
    float: right;
    display: inline-block;
}

.items {
    margin-top: 7mm;
}

.items td {
    border-left: 1px solid black;
}

.items tr td:last-child {
    border-right: 1px solid black;
}

.items thead td {
    border: 1px solid black;
    font-weight: bold;
    background-color: #ededed;
}

.items tbody tr td:first-child {
    width: 5mm;
}

.items tbody tr td:nth-child(2) {
    width: 10mm;
}

.items tbody tr td:nth-child(3) {
    width: 10mm;
}

.items tbody tr td:nth-child(4) {
    width: 40mm;
}


.items tbody tr td:nth-child(6) {
    width: 20mm;
}


.items tbody tr td:nth-child(7) {
    width: 10mm;
}

.items tbody tr td:nth-child(8) {
    width: 18mm;
}

.items tfoot td {
    border: 1px solid black;
    font-weight: bold;
}

.empty-line td {
    height: 0.5mm;
    padding: 0px !important;
}

.bottom-content {
    margin-top: 3mm;
}

.sinature p {
    margin-top: 5mm;
}

.sinature div {
    margin-top: 20mm;
    width: 50mm;
    padding-top: 1mm;
    border-top: 1px solid black;
    text-align: center;
    font-weight: bold;
}
