css:
body {
	padding: 0 !important;
}

.print-format {
    margin-left: 4mm;
    margin-right: 4mm;
    font-size: 9px;
}

.right {
    text-align: right;
}

.center {
    text-align: center;
}

.inline {
    display: inline;
}

.inline div {
    display: inline;
}

.inline p {
    display: inline;
}
.no-break-inside {
    page-break-inside: avoid;
}

.main {
    width: 100%;
}

.empty-header {
    height: 7mm;
}

.print-format td {
    padding: 4px !important;
}

.print-format p {
    margin: 0;
}

.u {
    text-decoration: underline;
    text-underline-offset: 3px    
}

table {
    width: 100%;
}

.header td {
    padding: 0 !important;
}

.header td:first-child {
    width: 55px;
}

.header td:nth-child(3) {
    width: 32%;
}

.header tr:first-child td:first-child img {
    padding-bottom: 4px !important;
    width: 130px;
}

.header tr:first-child td:last-child img {
    padding-bottom: 4px !important;
    width: 240px;
}

.header tr:nth-child(2) td:nth-child(-n+2),
.header tr:first-child td:last-child {
    padding-bottom: 4px !important;
}

.header tr:nth-child(2) td:first-child p {
    font-weight: bold;
    font-size: 15px;
}

.header tr:nth-child(3) td:nth-child(-n+2) {
    color: red;
}

.header tr:nth-child(3) td:first-child,
.header tr:nth-child(4) td:nth-child(-n+2) {
    font-weight: bold;
}

.header tr:nth-last-child(4) td,
.header tr:nth-last-child(5) td {
    padding-top: 3mm !important;
}

.party-name-addr,
.delivery-addr {
    display: inline-block;
    text-transform: uppercase;
    float: left;
}

.party-name-addr > div:first-child {
    float: left;
    display: inline-block;
}

.party-name-addr > div:nth-child(2) {
    float: right;
    display: inline-block;
}

.delivery-addr {
    margin-left: 5mm;
}

.header-preceeding-space {
    float: left;
    visibility: hidden;
}

.items {
    /*margin-top: 3.39mm;*/
}

.items td {
    border-left: 1px solid black;
}

.items tr td:last-child {
    border-right: 1px solid black;
}

.items thead td {
    border: 1px solid black;
    font-weight: bold;
    background-color: #ededed;
    vertical-align: middle !important;
}

.items tbody tr td:first-child {
    width: 5mm;
}

.items tbody tr td:nth-child(2) {
    width: 10mm;
}

.items tbody tr td:nth-child(3) {
    width: 35mm;
}

.items tbody tr td:nth-child(5) {
    width: 20mm;
}

.items tbody tr td:nth-child(6) {
    width: 20mm;
}

.items tfoot td {
    border: 1px solid black;
    font-weight: bold;
}

.item-description .ql-editor {
    line-height: 10px;
}

.empty-line td {
    height: 0.5mm;
    padding: 0px !important;
}

.bottom-content {
    margin-top: 3mm;
}

.terms table,
.terms table tr,
.terms table td {
    border: 0px !important;
    padding-left: 0px !important;
}

.terms table {
    margin: 2mm 0 !important;
}

.signature div:first-child {
    margin-top: 2mm;
    min-height: 25mm;
}

.signature div:nth-child(2) {
    width: 50mm;
    padding-top: 1mm;
    margin-top: 2mm;
    border-top: 1px solid black;
    text-align: left;
    font-weight: bold;
}

.signature .sign-company-name {
    font-size: 10px;
}

.sales-person-sign {
    width: 30mm;
}

.company-seal{
    width: 16mm;
}

.footer {
    height: 10mm;
}
