css:
body {
	padding: 0 !important;
}

.print-format {
    margin-left: 4mm;
    margin-right: 4mm;
    font-size: 9px;
}

.right {
    text-align: right;
}

.center {
    text-align: center;
}

.no-break-inside {
    page-break-inside: avoid;
}

.main {
    width: 100%;
}

.empty-header {
    height: 7mm;
}

.print-format td {
    padding: 4px !important;
}

.print-format p {
    margin: 0;
}

.u {
    text-decoration: underline;
    text-underline-offset: 3px    
}

table {
    width: 100%;
}

.va-mid,
.va-mid td {
    vertical-align: middle !important;
}

.header td {
    padding-left: 0 !important;
}

td.header-left,
td.header-right {
    padding-top: 0 !important;
}

.header-left {
    width: 68%;
}

.header-right {
    width: 32%;
}

.header-left table td:first-child {
    width: 17%;
}

.header-left img {
    /*margin-top: 5mm;*/
    width: 130px;
    /*height: 30px;*/
}

.header-left table tr:nth-child(2) p {
    font-weight: bold;
    font-size: 15px;
}

.header-left table tr:nth-child(3) td {
    color: red;
}

.header-left table tr:nth-child(3) td:first-child,
.header-left table tr:nth-child(4) td {
    font-weight: bold;
}

.header-left table tr:nth-child(1) td,
.header-left table tr:nth-child(3) td,
.header-left table tr:nth-child(4) td {
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}

.header-left table tr:nth-child(5) td {
    padding-top: 4mm !important;
    font-weight: bold;
    font-size: 10px;
}

.header-right img {
    width: 240px;
    /*height: 50px;*/
}

.header-right > div {
    padding-top: 4px;
    line-height: 12px;
}

.footer {
    height: 10mm;
}

.stock-status-list {
    border-collapse: collapse;
}

.stock-status-list .stock-status-party-info {
    width: 50%;
    font-weight: normal;
}

.bill-to {
    float: left;
    height: 100%;
    width: 50%;
}

.ship-to {
    float: right;
    height: 100%;
    width: 50%;
}


.stock-status-list-info tr:nth-child(1) td,
.stock-status-list-info tr:nth-child(2) td {
    font-size: 15px;
}

.stock-status-list-info tr:last-child td {
    border-bottom: 0;
}

.stock-status-list-info td {
    border: 1px solid black;
    border-collapse: collapse;
}

.stock-status-list td {
    border: 1px solid black;
    padding-top: 3px !important;
    padding-bottom: 3px !important;
}

.stock-status-list thead td {
    font-weight: bold;
}

.stock-status-list thead  td {
    text-align: center;
}

.stock-status-list tbody tr:last-child td div {
    height: 15mm;
}

.stock-status-list tr td:first-child {
    width: 7%;
}
